import React, { useContext, useEffect, useState } from 'react';
import {
  IonButton,
  IonButtons,
  IonContent,
  IonHeader,
  IonInput,
  IonLoading,
  IonPage,
  IonTitle,
  IonToolbar
} from '@ionic/react';
import { getLogger } from '../core';
import { StockContext } from './StockProvider';
import { RouteComponentProps } from 'react-router';
import { CarProps } from './CarProps';

const log = getLogger('StockEdit');

interface CarEditProps extends RouteComponentProps<{
  id?: string;
}> {}

const CarEdit: React.FC<CarEditProps> = ({ history, match }) => {
  const { cars, saving, savingError, saveCar } = useContext(StockContext);
  const [name, setName] = useState('');
  const [price, setPrice] = useState(0);
  const [available, setAvailable] = useState(false);
  const [stock, setStock] = useState<CarProps>();
  useEffect(() => {
    log('useEffect');
    const routeId = match.params.id || '';
    const stock = cars?.find(it => it.id === routeId);
    setStock(stock);
    if (stock) {
      setName(stock.name);
    }
  }, [match.params.id, cars]);
  const handleSave = () => {
    const editedStock = stock ? { ...stock, name, price, available} : {name, price, available};
    saveCar && saveCar(editedStock).then(() => history.goBack());
  };
  log('render');
  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Edit</IonTitle>
          <IonButtons slot="end">
            <IonButton onClick={handleSave}>
              Save
            </IonButton>
          </IonButtons>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonInput value={name} onIonChange={e => setName(e.detail.value || '')} />
        <IonLoading isOpen={saving} />
        {savingError && (
          <div>{savingError.message || 'Failed to save stock'}</div>
        )}
      </IonContent>
    </IonPage>
  );
};

export default CarEdit;
