import React, { useContext } from 'react';
import { RouteComponentProps } from 'react-router';
import {
  IonContent,
  IonFab,
  IonFabButton,
  IonHeader,
  IonIcon,
  IonList, IonLoading,
  IonPage,
  IonTitle,
  IonToolbar
} from '@ionic/react';
import { add } from 'ionicons/icons';
import Stock from './Stock';
import { getLogger } from '../core';
import { StockContext } from './StockProvider';

const log = getLogger('StockList');

const StockList: React.FC<RouteComponentProps> = ({ history }) => {
  const { stocks, fetching, fetchingError } = useContext(StockContext);
  log('render');
  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle class="ion-text-center">Stocks App</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonLoading isOpen={fetching} message="Fetching stocks" />
        {stocks && (
          <IonList>
            {stocks.map(({ id, name, price, available}) =>
              <Stock key={id} id={id} name={name} price={price} available={available}
                     onEdit={id => history.push(`/stock/${id}`)} />)}
          </IonList>
        )}
        {fetchingError && (
          <div>{fetchingError.message || 'Failed to fetch stocks'}</div>
        )}
        <IonFab vertical="bottom" horizontal="end" slot="fixed">
          <IonFabButton onClick={() => history.push('/stock')}>
            <IonIcon icon={add} />
          </IonFabButton>
        </IonFab>
      </IonContent>
    </IonPage>
  );
};

export default StockList;
